import React from 'react'
import { Link } from "react-router-dom";
import '../assets/css/itemListContainer.css'
import Button from '@mui/material/Button'
import Itemcount from './ItemCount';
import { contextoGeneral } from "../components/cartContext.jsx";
import { useContext } from 'react';
import RestarItem from './restarItem';
import { useState } from 'react';

export default function ItemCarrito({ item }) {
    const [product, setProduct] = useState("");

    return (
        <div className='tienda__grid__card'>
            <h3 className='tienda__grid__card__titulo'>{product.nombre} </h3>
            <p className='tienda__grid__card__parrafo'>Precio: ${product.precio}</p>
            <img className='tienda__grid__card__img' src={product.img} alt="Planta acuatica " />
            <div className='link'><Link to={"/item/" + product.id}>ir al item</Link></div>
            <RestarItem item={product}/>
        </div>
    )
}
