    const [guarda, setGuarda] = useState([]);

    useEffect(() => {
        localStorage.setItem(true, guarda);
            return <Container fixed>
                <h2 className="itemList" style={{ backgroundColor: color }}>{aludo}</h2>
            </Container>
    }, []);

}
