import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import {productosHC} from "../data/data.js";

export default function ItemDetailContainer() {
    const {iditem} = useParams();
    const [producto, setProducto] = useState({});

    useEffect(() => {
        //alert("cambio la categoria por eso salta de nuevo este efecto");
        const productoPromise = new Promise((res, rej) => {
            setTimeout(() => {
                res(productosHC.find((item) => item.id == iditem));
            }, 2000);
        });

        productoPromise.then((res) => {
            setProducto(res);
        });


    }, [iditem]);

    return (
        <div>
            {!producto.id && "Loading..."}
                <div key={producto.id}>
                    {JSON.stringify(producto)}
                    <br />
                    <br />
                </div>
        </div>
    );
}
