//@ts-check

import React, { useState, createContext } from "react";

export const contextoGeneral = createContext();

export default function CartContext({ children }) {
    const [carrito, setCarrito] = useState([]);
    
    
    function isInCart(id) { 
        const existInCart = carrito.findIndex((item) => item.id == id);
        return existInCart;
    }

    function addItem(item, quantity) { // agregar cierta cantidad de un ítem al carrito
        const pos = isInCart(item.id);
        if (pos == -1) {
            setCarrito([...carrito, { ...item, quantity }]);
        } else {
            const carritoAux = [...carrito];
            carritoAux[pos] = { ...carritoAux[pos], quantity: carritoAux[pos].quantity + quantity };
            setCarrito(carritoAux);
        }
    }

    function removeItem(){  // Remover un item del cart por usando su id
    //     let item;
    //     item = carrito.findIndex((product) => product.id === nuevoProducto.id);
    //     carrito.splice(item, 1);

    //     //const removeItem = (id) => setCart(cart.filter(product => product.id !== id));

    }

    function clear() { // Remover todos los items
        setCarrito([]);
    }




    return (
        <contextoGeneral.Provider value={{carrito, setCarrito, addItem, removeItem, clear, isInCart }}>
            {children}
        </contextoGeneral.Provider>
    );
}
