import React from 'react'
import Container from '@mui/material/Container'
import './itemList.css'
import { useState } from 'react';

export default function ItemListContainer({color, saludo}){  
    const [Acumulador, setAcumulador] = useState("0");
    
    const Sumar = (num) => {
        setAcumulador(parseInt(Acumulador) + 1);
    };

    return(
        <>
        <Container fixed>
        <h2 className="itemList"  style={{backgroundColor:color}}>{Acumulador}</h2>
        </Container>
        <Container fixed>
        <h2 className="itemList" onClick={Sumar} style={{backgroundColor:color}}>{Acumulador}</h2>
        </Container>
        <Container fixed>
        <h2 className="itemList" onClick={Restar} style={{backgroundColor:color}}>{Acumulador}</h2>
        </Container>
        </>
    )
}
