//@ts-check
import React, { useEffect, useState, createContext } from "react";

export const contextoGeneral = createContext();

export default function CartContext({ children }) {
    const [shoppingCart, setshoppingCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
    const [quantity, setQuantity] = useState("0");


    function actCart() {
        let count = 0;
        if (shoppingCart.length > 0) {
            shoppingCart.forEach(element => {
                count = count + element.cantidad
                setQuantity(parseInt(count));
            });
        } else {
            setQuantity(parseInt("0"));
        }
    }

    function isInCart(id) { 
        const existInCart = shoppingCart.findIndex((item) => item.id == id);
        return existInCart;
    }

    function addItem(item, cantidad) { // agregar cierta cantidad de un ítem al shoppingCart
        const pos = isInCart(item.id);
        if (pos == -1) {
            setshoppingCart([...shoppingCart, {...item, cantidad}]);
        } else {
            const shoppingCartAux = [...shoppingCart];
            shoppingCartAux[pos] = { ...shoppingCartAux[pos], cantidad: shoppingCartAux[pos].cantidad + cantidad };
            setshoppingCart(shoppingCartAux);
        }
    }

    function removeItem(id){  // Remover un item del cart por usando su id
        setshoppingCart(shoppingCart.filter(product => product.id !== id));
    }

    function clear() { // Remover todos los items
        setshoppingCart([]);
    }

    useEffect(() => {
        localStorage.setItem("cart", JSON.stringify(shoppingCart));
        actCart();
    }, [shoppingCart])


    return (
        <contextoGeneral.Provider value={{shoppingCart, quantity, actCart, setQuantity, setshoppingCart, addItem, removeItem, clear, isInCart }}>
            {children}
        </contextoGeneral.Provider>
    );
}
