export const productosHC = [
        { id: 100, name: "pelota", category: "deportes", precio: 100 },
        { id: 101, name: "arco", category: "deportes", precio: 100 },
        { id: 102, name: "pantalon", category: "deportes", precio: 100 },
        { id: 103, name: "vestido lola", category: "fiesta", precio: 200 },
        { id: 104, name: "cartera con diamantes", category: "fiesta", precio: 1000,},
    ];


export const GetProductos = (iditem) => {
    if (!iditem){
        return new Promise((res) => {
            setTimeout(() => {
                res(productosHC);
            }, 2000);
        });
    }else{
        return new Promise((res, rej) => {
            setTimeout(() => {
                res(productosHC.find((item) => item.id == iditem));
            }, 2000);
        });
    }
    
};
