import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import {GetProductos} from "../data/data.js";
import ItemList from "./ItemList";
import Container from '@mui/material/Container'

export default function ItemListContainer({color, saludo}) {

    const { idcategory } = useParams();
    const [productos, setProductos] = useState([]);
    
    const [guarda, setGuarda] = useState([]);

    useEffect(() => {
        localStorage.setItem(true, guarda);
        return (
            function saludo() {
                <Container fixed>
                    <h2 className="itemList" style={{ backgroundColor: color }}>{saludo}</h2>
                </Container>
            }
        )
    }, []);


    useEffect(() => {
        
        GetProductos() //llama a la funcion del data
            .then((res) => {
                if (idcategory) {
                    setProductos(res.filter((item) => item.category == idcategory));
                } else {
                    setProductos(res);
                }
            });
    }, [idcategory]);

    return (
        <ItemList productos={productos}/>
    );
}
